<script>

var vuInt;
var needle = document.getElementById( 'vuneedle' );
function vu( range, ms ) {
	var range = range || 12; // -/+
	var deg = 0;
	var inc;
	clearInterval( vuInt );
	vuInt = setInterval( function() {
		inc = Math.random() * range * 2;
		deg += inc;
		if ( deg < -range ) {
			deg = -range + inc;
		} else if ( deg > range ) {
			deg = range - inc;
		}
		needle.style.transform = 'rotate( '+ deg +'deg )';
	}, ms || 500 );
}
function vuStop() {
	clearInterval( vuInt );
	needle.style.transform = 'rotate( -29.4deg )';
}
vu();

</script>
